import React from 'react';
import {Component} from 'react';
import './App.css';

function Item(props){
  return(
    <li key={props.key} className='todoItem'>
      <p>{props.value}</p>
    </li>
  )
}

class App extends Component {
  constructor(props) {
    super(props);
    this.state = {
      lenght: 0,
      taskList: [],
    };
  }
  addNewTask(itemText){
    let newTaskList = this.state.taskList.concat([
      {
        text: itemText,
        key: new Date().toLocaleString(),
      }
    ])
    this.setState({
      lenght: this.state.lenght + 1,
      taskList: newTaskList,
    });
    return ;
  }

  render(){
    const list = this.state.lenght != 0 ? this.state.taskList.map((item, index) => {
      return (
        <Item 
          key={this.props.taskList[index].key}
          value={this.props.taskList[index].text}
        />
      ); 
    }) : '';

    return (
      <div className="App">
        <h1>to-do list</h1>
        <input 
          type='text'
          onKeyPress={event => {
                if (event.key === 'Enter') {
                  this.addNewTask(event.target.value);
                  event.target.value = '';
                }
              }}
          defaultValue=''
        >
        </input>
        <ul className='itemList'>
          {list}
        </ul>
      </div>
    );
  }
}

export default App;
